function isEmpty(object) {
  for(var key in object) {
    if(object.hasOwnProperty(key)){
      return false;
    }
  }
  return true;
}

done(steps['9-endpoint-update-body'] != null && !isEmpty(steps['9-endpoint-update-body'].body));

var body;
if (steps['9-endpoint-update-body']) {
  body = steps['9-endpoint-update-body'].body;
} else {
  body = {}
}

var ceFieldName = steps['4-loop-over-monitored-fields'].entry.ceFieldName;
if(ceFieldName != null){
  body[ceFieldName] = steps['3-get-object'].response.body[steps['4-loop-over-monitored-fields'].entry.monitoredFieldName];
}

done({
  body: body
});

var kmRequest = false;
var body = {};
var path;

//Do they want to process an event? 
if (steps['4-loop-over-monitored-fields'].entry.kmEventName != null) {
  var desiredEventFieldValue = steps['4-loop-over-monitored-fields'].entry.fieldValueForEvent;
  var eventFieldValue = steps['3-get-object'].response.body[steps['4-loop-over-monitored-fields'].entry.monitoredFieldName];

  // Has the event happened? 
  if (eventFieldValue === desiredEventFieldValue) {
    kmRequest = true;
    path = 'recorded-events';

    //prepare post data for an event
    body['_p'] =steps['3-get-object'].response.body[config.unique.id.field.name];
    body['_n'] = steps['4-loop-over-monitored-fields'].entry.kmEventName;

    //Do they also want to process a property? 
    if (steps['4-loop-over-monitored-fields'].entry.kmPropertyName != null) {
      //add post data for a property
      var kmField = steps['4-loop-over-monitored-fields'].entry.kmPropertyName;
      body[kmField] = steps['3-get-object'].response.body[steps['4-loop-over-monitored-fields'].entry.monitoredFieldName];

    }

    done({
      kmRequest: kmRequest,
      body: body,
      path: path
    });

  }
}

//If we are here, then either they don't want to process an event, or the event hasn't happened
//Do they want to process a property?
if (steps['4-loop-over-monitored-fields'].entry.kmPropertyName != null) {
  kmRequest = true;
  path = 'properties';
  body['_p'] = steps['3-get-object'].response.body[config.unique.id.field.name];

  var kmField = steps['4-loop-over-monitored-fields'].entry.kmPropertyName;
  body[kmField] = steps['3-get-object'].response.body[steps['4-loop-over-monitored-fields'].entry.monitoredFieldName];

}

done({
  kmRequest: kmRequest,
  body: body,
  path: path
});

done({
  body: {
    objectId: trigger.event.objectId,
    objectType: trigger.event.objectType,
    hubName: config.hub.name
  },
  headers: {
    'Sforce-Auto-Assign': 'false'
  }
});

var monitoredFieldName = steps['4-loop-over-monitored-fields'].entry.monitoredFieldName;
var ceFieldName = steps['4-loop-over-monitored-fields'].entry.ceFieldName;

var monitoredFieldValue = steps['3-get-object'].response.body[monitoredFieldName];
var ceFieldValue = steps['3-get-object'].response.body[ceFieldName];

done(monitoredFieldValue != ceFieldValue);
