var incident = steps['getIncidentCase'].response.body;

var q = "Id='"+ incident.OwnerId +"'";

done({where: q});

var status = steps.getIncidentCase.response.body.Status;
// var closed = steps.getIncidentCase.response.body.ClosedDate ? steps.getIncidentCase.response.body.ClosedDate : "";
var incident = steps.getIncidentCase.response.body;
// var humandate = new Date(closed).toDateString();
var ticketId = steps.getIncidentCase.response.body.Id;
// var owner = steps['get-user'].response.body.Name;
var ticket = steps['getIncidentCase'].response.body.CaseNumber;
var lastUpdated = incident.LastModifiedDate;


// if(owner){
//   agent = owner;
// }else{
//   agent = "";
// }

var owner = '';
if(steps['get-user'].response.body.Name){
  owner = steps['get-user'].response.body.Name;
} else{
  owner = steps['get-queue'].response.body[0].Name;
}


// done( {"handles" : 
//           {"crm" : [
//                     {"id":ticketId,
//                     "networkinstance":"Case Escalation",
//                     "ticket":ticket,
//                     "closedate": humandate,
//                     "agentassigned":agent,
//                     "status":status
//                     }
//                   ]
//           }
//       }
    // )

var payload = {
    "handles" : 
          {"crm" : [
                    {"id":ticketId,
                    "networkinstance":config.caseintegrationname,
                    "ticket":ticket,
                    "lastupdated":new Date(lastUpdated).toDateString(),
                    "agentassigned":owner,
                    "status":status
                    }
                   ]
          }
}

if(incident.ClosedDate){
  payload.handles.crm[0].closedate = new Date(incident.ClosedDate).toDateString();
}

done(payload);

// var body = 'An error occured in the Ticket status formula <br><br>Instance Name: '+ info.formulaInstanceName + '<br>Instance ID: '+info.formulaInstanceId+'<br>Execution ID: '+info.formulaExecutionId+'<br><br>';

// notify.email(config.email, 'Ticket Status Error', body);
// done(true);
function isFailedResponse (step) {
  return (step && step.response && step.response.code && step.response.code != "200");
}

var msg = '';
var errorStep;

var stepArray = ['getIncidentCase','get-user','get-queue','patchToAuthor'];
for(var i=0;i<stepArray.length;i++){
  if(isFailedResponse(steps[stepArray[i]])){
    msg = steps[stepArray[i]].response.body.message;
    errorStep = stepArray[i];
  }
}


var samplePayload = {
  "events": [
    {
      "objectType": "Case",
      "objectId": trigger.event.objectId,
      "eventType": "UPDATED",
      "elementKey": "sfdc",
      "hubKey": "crm"
    }
  ],
  "instance_id": info.formulaInstanceId
}

var body = 'An error occured in the Ticket status formula <br><br>Instance Name: '+ info.formulaInstanceName + '<br>Instance ID: '+info.formulaInstanceId+'<br>Execution ID: '+info.formulaExecutionId+'<br><br>';

body = body + 'Step that errored: '+ errorStep + '<br>Response: ' + msg +'<br><br><br>Please see below payload to assist in retriggering formula<br><br>' + samplePayload;

notify.email(config.email, 'Ticket Status Error', body);
done(true);

var type = trigger.event.objectType;
var eventType = trigger.event.eventType;

if((type === 'Case') && (eventType === 'UPDATED')){
  done(true);
}
done(false);

var subject = steps.getIncidentCase.response.body.Subject;

if(subject == 'Social Escalation'){
  done(true);
}else{
  done(false);
}

let caseResponse = steps['get-user'].response.code;

if(caseResponse === 200){
  done(true);
}else{
  done(false);
}

var subject = steps.getIncidentCase.response.body.Subject;
var description = steps.getIncidentCase.response.body.Description ? steps.getIncidentCase.response.body.Description : "N/A";
var parseArr = [];
var authorId;


if(subject.indexOf('Social Escalation')!=-1){
if(description.indexOf('(LRID)')!=-1){
  parseArr = description.split('(LRID)');
  authorId = parseArr[1].substring(1,37);
  done({"authorId":authorId});
} 
}

done({"authorId":"none"});
